mapper.select:
select *
from sys_order


        and owner > #{startOwner}


        and owner < #{endOwner}


        and customer_name like CONCAT('%',#{customerName},'%')


        and channel_name like CONCAT('%',#{channelName},'%')


        and operation = #{operation}

select (sum(three_class) + sum(four_class) + sum(five_class)) as peopleCount, sum(three_class) as threeClassNum, sum(four_class) as fourClassNum, sum(five_class) as fiveClassNum, sum(three_class_price) as threeClassTotal, sum(four_class_price) as fourClassTotal, sum(five_class_price) as fiveClassTotal, sum(total_rebate) as totalRebate, sum(profit) as totalProfit from sys_order
and owner > #{startOwner}
and owner < #{endOwner}
and customer_name like CONCAT('%',#{customerName},'%')
and channel_name like CONCAT('%',#{channelName},'%')
and operation = #{operation}

select * from sys_order


        and owner > #{startTime}


        and owner < #{endTime}


        and operation = #{userId}
